INSERT INTO `information` (`id`, `img`, `title`, `content`, `time`, `username`, `num`) VALUES (2, 'http://localhost:9090/files/download/1737907862157-Image_1716109267938.jpg', '资讯2', '<p><br></p><p style=\"text-align: start;\"><strong>标题：</strong> 测试富文本编辑器功能</p><p style=\"text-align: start;\"><strong>段落 1：</strong><br><br>富文本编辑器是一种可以处理格式化文本、插入图片、链接、视频等多种元素的工具。它通常用于内容管理系统（CMS）、博客、论坛和其他网页编辑功能。</p><p style=\"text-align: start;\"><strong>段落 2：</strong><br><br>它支持不同的文本样式，如加粗、斜体、下划线、字体颜色变化等，帮助用户更方便地编辑和呈现内容。</p><p style=\"text-align: start;\">例如：</p><p style=\"text-align: start;\"><strong>段落 3：</strong><img src=\"http://localhost:9090/files/download/1738114384193-Image_1717860736180.jpg\" alt=\"\" data-href=\"\" style=\"width: 50%;\"><br><br>此外，富文本编辑器也支持插入表格、代码块、项目符号列表、数字列表等格式。这里是一个简单的项目符号列表：</p><ul><li style=\"text-align: start;\">项目 1</li><li style=\"text-align: start;\">项目 2</li><li style=\"text-align: start;\">项目 3</li></ul><p style=\"text-align: start;\"><strong>段落 4：</strong><br><br>如果你需要插入一个链接，也可以轻松实现：<a href=\"https://space.bilibili.com/264369673?spm_id_from=333.1007.0.0\" target=\"_blank\">访问我的空间</a>。</p>', '2025-01-26 02:35:45', 'admin001', 27);
INSERT INTO `information` (`id`, `img`, `title`, `content`, `time`, `username`, `num`) VALUES (3, 'http://localhost:9090/files/download/1737907871026-Image_1716109357062.jpg', '资讯1', '<p>资讯1</p>', '2025-01-26 02:35:45', 'admin001', 5);
INSERT INTO `information` (`id`, `img`, `title`, `content`, `time`, `username`, `num`) VALUES (4, 'http://localhost:9090/files/download/1737907879066-Image_1716109361705.jpg', '测试文本1', '<h3 style=\"text-align: start;\">第一段：标题和段落</h3><p style=\"text-align: start;\"><strong>标题：</strong> 高效学习的技巧</p><p style=\"text-align: start;\"><strong>段落 1：</strong><br><br>学习不仅仅是读书，更多的是思考和实践。高效学习的核心在于理解和应用，尤其是记忆技巧和时间管理。学会如何有效整理笔记，并与他人分享学习成果，可以极大地提高学习效率。</p><p style=\"text-align: start;\"><strong>段落 2：</strong><br><br>另外，学习时要尽量避免分心，集中注意力在一段时间内专注于一件事情。短时间的深度学习通常比长时间的浅尝辄止效果更好。<br></p>', '2025-01-26 03:21:52', 'admin001', 5);
INSERT INTO `information` (`id`, `img`, `title`, `content`, `time`, `username`, `num`) VALUES (5, 'http://localhost:9090/files/download/1737907886028-Image_1716129295432.jpg', '测试文本2', '<h3 style=\"text-align: start;\">第二段：列表和图片</h3><p style=\"text-align: start;\"><strong>标题：</strong> 健康饮食小贴士</p><p style=\"text-align: start;\"><strong>段落 1：</strong><br><br>健康的饮食习惯对于维持身体健康至关重要。以下是一些常见的健康饮食小贴士：</p><ul><li style=\"text-align: start;\"><strong>多喝水</strong>，保持身体的水分平衡</li><li style=\"text-align: start;\"><strong>多吃蔬菜和水果</strong>，保证膳食的多样性</li><li style=\"text-align: start;\"><strong>减少糖分摄入</strong>，避免过多的空白热量</li><li style=\"text-align: start;\"><strong>适量运动</strong>，配合饮食增强体质</li></ul><p style=\"text-align: start;\"><strong>段落 2：</strong><br><br>在饮食中加入一些健康的食材，如全麦食品、坚果和鱼类，能够提供更多的营养和抗氧化物质。</p>', '2025-01-26 03:21:52', 'admin001', 8);
INSERT INTO `information` (`id`, `img`, `title`, `content`, `time`, `username`, `num`) VALUES (6, 'http://localhost:9090/files/download/1737907892806-Image_1716129299538.jpg', '测试文本3', '<h3 style=\"text-align: start;\">第三段：引用和链接</h3><p style=\"text-align: start;\"><strong>标题：</strong> 引用经典名言</p><p style=\"text-align: start;\"><strong>段落 1：</strong><br><br>“<strong>生活就像一盒巧克力，你永远不知道下一个是什么味道</strong>。”<br><br>——《阿甘正传》</p><p style=\"text-align: start;\">这句经典的名言深刻地传达了生活的不确定性和无限可能性。它鼓励我们拥抱未知和冒险，接受每一个即将到来的挑战。</p><p style=\"text-align: start;\"><strong>段落 2：</strong><br><br>你可以在这里找到更多关于这部电影的内容：<a href=\"https://www.imdb.com/title/tt0109830/\" target=\"_blank\">阿甘正传电影</a></p>', '2025-01-26 03:21:52', 'admin001', 9);
INSERT INTO `information` (`id`, `img`, `title`, `content`, `time`, `username`, `num`) VALUES (7, 'http://localhost:9090/files/download/1737907902221-Image_1717166782872.jpg', '测试文本4', '<h3 style=\"text-align: start;\">第二段：列表和图片</h3><p style=\"text-align: start;\"><strong>标题：</strong> 健康饮食小贴士</p><p style=\"text-align: start;\"><strong>段落 1：</strong><br><br>健康的饮食习惯对于维持身体健康至关重要。以下是一些常见的健康饮食小贴士：</p><ul><li style=\"text-align: start;\"><strong>多喝水</strong>，保持身体的水分平衡</li><li style=\"text-align: start;\"><strong>多吃蔬菜和水果</strong>，保证膳食的多样性</li><li style=\"text-align: start;\"><strong>减少糖分摄入</strong>，避免过多的空白热量</li><li style=\"text-align: start;\"><strong>适量运动</strong>，配合饮食增强体质</li></ul><p style=\"text-align: start;\"><strong>段落 2：</strong><br><br>在饮食中加入一些健康的食材，如全麦食品、坚果和鱼类，能够提供更多的营养和抗氧化物质。</p>', '2025-01-26 03:32:42', 'admin001', 2);
INSERT INTO `information` (`id`, `img`, `title`, `content`, `time`, `username`, `num`) VALUES (8, 'http://localhost:9090/files/download/1737907908943-Image_1717006155748.jpg', '测试文本5', '<h3 style=\"text-align: start;\">第四段：代码块和格式化文本</h3><p style=\"text-align: start;\"><strong>标题：</strong> 使用代码提高生产力</p><p style=\"text-align: start;\"><strong>段落 1：</strong><br><br>如果你是开发者，你可以通过编写简洁的代码来提升工作效率。以下是一个简单的 Python 示例，展示了如何定义函数：</p><pre><code class=\"language-java\">public class HelloWorld {\n    public static void main(String[] args) {\n        // 输出 Hello, World!\n        System.out.println(\"Hello, World!\");\n    }\n}</code></pre><p><br></p>', '2025-01-26 03:35:59', 'admin001', 2);
INSERT INTO `information` (`id`, `img`, `title`, `content`, `time`, `username`, `num`) VALUES (9, 'http://localhost:9090/files/download/1737907927632-Image_1717237362087.jpg', '测试文本6', '<h3 style=\"text-align: start;\">第五段：有序列表和表格</h3><p style=\"text-align: start;\"><strong>标题：</strong> 旅行计划</p><p style=\"text-align: start;\"><strong>段落 1：</strong><br><br>这是我的旅行计划，包括需要携带的物品和日程安排：</p><p style=\"text-align: start;\"><strong>有序列表：</strong></p><ol><li style=\"text-align: start;\">带上护照和签证</li><li style=\"text-align: start;\">准备旅行行李</li><li style=\"text-align: start;\">预定机票和住宿</li><li style=\"text-align: start;\">确认旅行保险</li></ol><p style=\"text-align: start;\"><strong>表格：</strong></p><table style=\"width: 100%; text-align: start;\"><tbody><tr><th colspan=\"1\" rowspan=\"1\" width=\"auto\">日期</th><th colspan=\"1\" rowspan=\"1\" width=\"auto\">活动</th><th colspan=\"1\" rowspan=\"1\" width=\"auto\">备注</th></tr><tr><td colspan=\"1\" rowspan=\"1\" width=\"auto\">2025/05/01</td><td colspan=\"1\" rowspan=\"1\" width=\"auto\">飞往东京</td><td colspan=\"1\" rowspan=\"1\" width=\"auto\">按时起飞</td></tr><tr><td colspan=\"1\" rowspan=\"1\" width=\"auto\">2025/05/02</td><td colspan=\"1\" rowspan=\"1\" width=\"auto\">游览东京塔</td><td colspan=\"1\" rowspan=\"1\" width=\"auto\">天气晴朗</td></tr><tr><td colspan=\"1\" rowspan=\"1\" width=\"auto\">2025/05/03</td><td colspan=\"1\" rowspan=\"1\" width=\"auto\">参观博物馆</td><td colspan=\"1\" rowspan=\"1\" width=\"auto\">需提前购票</td></tr></tbody></table><p><br></p>', '2025-01-26 03:37:04', 'admin001', 3);
